import "core-js/modules/es6.array.find";
import jQuery from 'jquery';
var instance = {};

instance.setMaxHeight = function () {
  jQuery(function () {
    setTimeout(function () {
      var showItem = jQuery('.show-item');
      showItem.each(function () {
        var _this = jQuery(this);

        var height = _this.find('.dropdown-menu').outerHeight();

        _this.css({
          minHeight: height + 50
        });
      });
    }, 500);
  });
};

instance.reloadPage = function () {
  jQuery(function () {
    setTimeout(function () {
      jQuery('.sidebar-group-items li a[href="/components/scrollspy/"]').click(function () {
        var url = '/components/scrollspy/';
        jQuery(location).attr('href', url);
      });
    }, 500);
  });
};

export default instance;
